import React from "react";

export default class PostQuestion extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    console.log(event.target.value);
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert("A name was submitted: " + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div className="row">
        <div className="col-md-6">
          <form onSubmit={this.handleSubmit}>
            <label>
              Description:
              <input
                type="text"
                name="description"
                onChange={this.handleChange}
              />
            </label>
            <div className="d-flex align-items-center">
              <label>
                First option:
                <input
                  type="text"
                  name="option_one"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="0"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label>
                Second option:
                <input
                  type="text"
                  name="option_two"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="1"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label>
                Third option:
                <input
                  type="text"
                  name="option_three"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="2"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label>
                Forth option:
                <input
                  type="text"
                  name="option_four"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="3"
                onChange={this.handleChange}
              />
            </div>
            <input type="submit" value="Submit" />
          </form>
        </div>
      </div>
    );
  }
}
