import React from "react";

export default class PostQuestion extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      description: "",
      option_one: "",
      option_two: "",
      option_three: "",
      option_four: "",
      option: "",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    console.log(event.target.value);
    this.setState({ [event.target.value]: event.target.value });
  }

  handleSubmit(event) {
    alert("A name was submitted: " + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div className="row">
        <div className="col-md-6">
          <form onSubmit={this.handleSubmit}>
            <label className="d-flex flex-column align-items-start">
              Description:
              <input
                type="text"
                name="description"
                onChange={this.handleChange}
              />
            </label>
            <div className="d-flex align-items-center">
              <label className="d-flex flex-column align-items-start">
                First option:
                <input
                  type="text"
                  name="option_one"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="0"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label className="d-flex flex-column align-items-start">
                Second option:
                <input
                  type="text"
                  name="option_two"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="1"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label className="d-flex flex-column align-items-start">
                Third option:
                <input
                  type="text"
                  name="option_three"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="2"
                onChange={this.handleChange}
              />
            </div>
            <div className="d-flex align-items-center">
              <label className="d-flex flex-column align-items-start">
                Forth option:
                <input
                  type="text"
                  name="option_four"
                  onChange={this.handleChange}
                />
              </label>
              <input
                type="radio"
                name="option[]"
                value="3"
                onChange={this.handleChange}
              />
            </div>
            <input type="submit" value="Submit" />
          </form>
        </div>
      </div>
    );
  }
}
